import { Link, NavLink, useLocation, useNavigate } from "react-router-dom";

import { sidebarLinks } from "../constant";
import Button from "@mui/material/Button";
import profile from "../assets/icons/profile-placeholder.svg"
import LogoutIcon from '@mui/icons-material/Logout';

const LeftSideBar = () => {
  const navigate = useNavigate();
  const { pathname } = useLocation();
  //const { user, setUser, setIsAuthenticated, isLoading } = useUserContext();

  //const { mutate: signOut } = useSignOutAccount();

  const handleSignOut = async (e) => {
    e.preventDefault();
   // signOut();
   // setIsAuthenticated(false);
   // setUser(INITIAL_USER);
    navigate("/sign-in");
  };
  const handleClick=()=>{
    navigate("/profile");
  }

  return (
    <nav className="leftsidebar">
      <div className="flex flex-col gap-11">
        <Link to="/" className="flex gap-3 items-center">
          <h1 className="font-bold text-xl">Knowledge Hive</h1>
        </Link>

       {/* {isLoading || !user.email ? (
          <div className="h-14">
            <Loader />
          </div>
        ) : (
          )}
        */}
          <Link to={'/profile/'} className="flex gap-3 items-center" onClick={handleClick}>
            <img
              src={profile}
              alt="profile"
              className="h-14 w-14 rounded-full"
            />
            <div className="flex flex-col">
              <p className="body-bold">Vedanshi</p>
              <p className="small-regular text-light-3">@vedanshi</p>
            </div>
          </Link>
        

        <ul className="flex flex-col gap-6">
          {sidebarLinks.map((link) => {
            const isActive = pathname === link.route;

            return (
              <li
                key={link.label}
                className={`leftsidebar-link group ${
                  isActive && "bg-primary-500"
                }`}>
                <NavLink
                  to={link.route}
                  className="flex gap-4 items-center p-4">
                 <img
                    src={link.imgURL}
                    alt={link.label}
                    className={`group-hover:invert-white ${
                      isActive && "invert-white"
                    }`}
                  />
                  {link.label}
                </NavLink>
              </li>
            );
          })}
        </ul>
      </div>

      <Button
        variant="ghost"
        className="shad-button_ghost"
        onClick={(e) => handleSignOut(e)}>
        <LogoutIcon/>
        <p className="small-medium lg:base-medium">Logout</p>
      </Button>
    </nav>
  );
};

export default LeftSideBar;
