import React from "react";
import FileUploader from "../../shared/FileUploader";
import { useNavigate } from "react-router-dom";
import { convertFileToUrl } from "../../libs/utils.ts";
import { useCallback, useState,useRef } from "react";
import { useDropzone } from "react-dropzone";
import Button from "@mui/material/Button";
import PropTypes from "prop-types";
import fileupload from "../../assets/icons/file-upload.svg";
import axios from 'axios';

export default function PostForm({ fieldChange, mediaUrl }) {
  const navigate = useNavigate();

  const inputRef=useRef(null);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [link, setLink] = useState('');
  const [postfile, setPostFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('idle'); // 'idle', 'uploading', 'success', 'error'

  const handleTitleChange = (event) =>{
    console.log(event.target.value);
    setTitle(event.target.value);
  }
  const handleContentChange = (event) => setContent(event.target.value);
  const handleLinkChange = (event) => setLink(event.target.value);
  const handleFileChange = (event) =>{
    console.log(event.target.files);
    setPostFile(event.target.files[0]); // Select the first file
  }

  const handleImageClick=()=>{
    inputRef.current.click()
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!title || !content) {
      console.error('Please fill in all fields and select a file.');
      return;
    }

    console.log("Uploading");
    setUploadStatus('uploading'); // Update UI to show progress

    try {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('link', link);
      formData.append('content', content);
      formData.append('file', postfile);

      const response = await axios.post('/api/posts', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('Post uploaded successfully:', response.data);
      setUploadStatus('success'); 
      setTitle('');
      setLink('');
      setContent('');
      setPostFile(null);
    } catch (error) {
      console.error('Error uploading post:', error);
      setUploadStatus('error'); 
    }
  }
    //Upload file

  const fieldChangePropType = PropTypes.func; // Basic function type

  fieldChangePropType.propTypes = {
    files: PropTypes.arrayOf(PropTypes.instanceOf(File)),
  };

  FileUploader.propTypes = {
    mediaUrl: PropTypes.string, // Required string prop
  };
  const [file, setFile] = useState([]);
  const [fileUrl, setFileUrl] = useState(mediaUrl);

  const onDrop = useCallback(
    (acceptedFiles) => {
      setFile(acceptedFiles);
      fieldChange(acceptedFiles);
      setFileUrl(convertFileToUrl(acceptedFiles[0]));
    },
    [file]
  );

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: {
      "image/*": [".png", ".jpeg", ".jpg"],
    },
  });

  return (
    <form className="flex flex-col gap-9 w-full max-w-5xl" onSubmit={handleSubmit}>
      <div className="space-y-12">
        <div className="border-b border-gray-900/10 pb-12">
          <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
            <div className="sm:col-span-8">
              <label className="shad-form_label">Post Title</label>
              <div className="mt-2">
                <textarea
                  className="custom-scrollbar shad-form_message w-full h-10  bg-dark-3 rounded-xl border-none focus-visible:ring-1 focus-visible:ring-offset-1 ring-offset-light-3"
                  type="text"
                  name="title"
                  id="tile"
                  autoComplete="title"
                  placeholder="Write your post title here"
                  value={title}
                  onChange={handleTitleChange}
                />
              </div>
            </div>

            <div className="sm:col-span-8">
              <label className="shad-form_label">Post Link</label>
              <div className="mt-2">
                <textarea
                  className="custom-scrollbar shad-form_message w-full h-10  bg-dark-3 rounded-xl border-none focus-visible:ring-1 focus-visible:ring-offset-1 ring-offset-light-3"
                  type="text"
                  name="link"
                  id="link"
                  autoComplete="link"
                  placeholder="Post your link here"
                  value={link}
                  onChange={handleLinkChange}
                />
              </div>
            </div>

            <div className="sm:col-span-8">
              <label className="shad-form_label">Post Description</label>
              <div className="mt-2">
                <textarea
                  className="shad-textarea custom-scrollbar shad-form_message w-full"
                  type="text"
                  name="description"
                  id="description"
                  autoComplete="description"
                  placeholder="Write your post description here"
                  value={content}
                  onChange={handleContentChange}
                />
              </div>
            </div>

            <div className="sm:col-span-8">
              <label className="shad-form_label">Add Photos</label>
              <div className="mt-2" onClick={handleImageClick}>
                <div
                  {...getRootProps()}
                  className="flex flex-center flex-col bg-dark-3 rounded-xl cursor-pointer"
                >
                  <input {...getInputProps()} className="cursor-pointer" value={postfile}
                  onChange={handleFileChange} ref={inputRef}/>

                  {fileUrl ? (
                    <>
                      <div className="flex flex-1 justify-center w-full p-5 lg:p-10">
                        <img
                          src={fileUrl}
                          alt="image"
                          className="file_uploader-img"
                        />
                      </div>
                      <p className="file_uploader-label">
                        Click or drag photo to replace
                      </p>
                    </>
                  ) : (
                    <div className="file_uploader-box ">
                      <img
                        src={fileupload}
                        width={96}
                        height={77}
                        alt="file upload"
                      />

                      <h3 className="base-medium text-light-2 mb-2 mt-6">
                        Drag photo here
                      </h3>
                      <p className="text-light-4 small-regular mb-6">
                        SVG, PNG, JPG
                      </p>

                      <Button type="button" className="shad-button_dark_4">
                        Select from computer
                      </Button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="mt-6 flex items-center justify-end gap-x-6">
        <button
          type="button"
          className="text-sm font-semibold leading-6 text-white"
          onClick={() => navigate("/create-post")}
        >
          Cancel
        </button>
        <button
          type="submit"
          className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
        >
          Submit
        </button>
      </div>
    </form>
  );
}
