import {
  Route,
  Routes,
  Link,
  Outlet,
  useParams,
  useLocation,
} from "react-router-dom";
import React from "react";
import Button from "@mui/material/Button";
import Loader from "../../shared/Loader"
import profile from "../../assets/icons/profile-placeholder.svg"
//import { LikedPosts } from "@/_root/pages";
//import { useUserContext } from "@/context/AuthContext";
//import { useGetUserById } from "@/lib/react-query/queries";
//import { GridPostList, Loader } from "@/components/shared";

//interface StabBlockProps {
//  value: string | number;
//  label: string;
//}

const StatBlock = ({ value, label }) => (
  <div className="flex-center gap-2">
    <p className="small-semibold lg:body-bold text-primary-500">{value}</p>
    <p className="small-medium lg:base-medium text-light-2">{label}</p>
  </div>
);

const Profile = ({id, name, username, imgUrl, bio}) => {
 {/*  //const { id } = useParams();
  const UserContext = React.createContext(null);
  const { pathname } = useLocation();

  //const { data: currentUser } = useGetUserById(id || "");

  //if (!user)
  // return (
  //   <div className="flex-center w-full h-full">
  //      <Loader />
  //   </div>
  //  );

  return (
    <div className="profile-container">
      <div className="profile-inner_container">
        <div className="flex xl:flex-row flex-col max-xl:items-center flex-1 gap-7">
          <img
            src={
              imgUrl || profile
            }
            alt="profile"
            className="w-28 h-28 lg:h-36 lg:w-36 rounded-full"
          />
          <div className="flex flex-col flex-1 justify-between md:mt-2">
            <div className="flex flex-col w-full">
              <h1 className="text-center xl:text-left h3-bold md:h1-semibold w-full">
              {name}
              </h1>
              <p className="small-regular md:body-medium text-light-3 text-center xl:text-left">
                @{username}
              </p>
            </div>

            <div className="flex gap-8 mt-10 items-center justify-center xl:justify-start flex-wrap z-20">
              <StatBlock value={0} label="Posts" />
              <StatBlock value={20} label="Followers" />
              <StatBlock value={20} label="Following" />
            </div>

            <p className="small-medium md:base-medium text-center xl:text-left mt-7 max-w-screen-sm">
            {bio}
            </p>
          </div>

          <div className="flex justify-center gap-4">
            <div className={`${id && "hidden"}`} >                   
              <Link
                to={`/update-profile/${$id}`}  
                className={`h-12 bg-dark-4 px-5 text-light-1 flex-center gap-2 rounded-lg ${
                  id && "hidden"
                }`}>
                <img
                  src={"/assets/icons/edit.svg"}
                  alt="edit"
                  width={20}
                  height={20}
                />
                <p className="flex whitespace-nowrap small-medium">
                  Edit Profile
                </p>
              </Link>
            </div>
            <div className={`${id === id && "hidden"}`} >
              <Button type="button" className="shad-button_primary px-8">
                Follow
              </Button>
            </div>
          </div>
        </div>
      </div>

      {id && (
        <div className="flex max-w-5xl w-full">
          <Link
            to={`/profile/${id}`}
            className={`profile-tab rounded-l-lg ${
              pathname === `/profile/${id}` && "!bg-dark-3"
            }`}>
            <img
              src={"/assets/icons/posts.svg"}
              alt="posts"
              width={20}
              height={20}
            />
            Posts
          </Link>
          <Link
            to={`/profile/${id}/liked-posts`}
            className={`profile-tab rounded-r-lg ${
              pathname === `/profile/${id}/liked-posts` && "!bg-dark-3"
            }`}>
            <img
              src={"/assets/icons/like.svg"}
              alt="like"
              width={20}
              height={20}
            />
            Liked Posts
          </Link>
        </div>
     )}
       {/* 
      <Routes>
        <Route
          index
          element={<GridPostList posts={''} showUser={false} />}
        />
          <Route path="/liked-posts" element={<LikedPosts />} />
       
      </Routes>
      */}
    //  <Outlet />
   // </div>
  
 // );

};

export default Profile;
