import React from "react"
import FileUploader from "../../shared/FileUploader"
import { useNavigate } from 'react-router-dom';
import { zodResolver } from "@hookform/resolvers/zod";

export default function PostForm({post , action}) {
  const navigate = useNavigate();
  const form = useForm<z.infer<typeof PostValidation>>({
    resolver: zodResolver(PostValidation),
    defaultValues: {
      caption: post ? post?.caption : "",
      file: [],
      location: post ? post.location : "",
      tags: post ? post.tags.join(",") : "",
    },
  });

  const handleSubmit = async (value) => {
    // ACTION = UPDATE
    if (post && action === "Update") {
      const updatedPost = await updatePost({
        ...value,
        postId: post.$id,
        imageId: post.imageId,
        imageUrl: post.imageUrl,
      });

      if (!updatedPost) {
        console.log("Post failed")
      }
      return navigate(`/posts/${post.$id}`);
    }

    // ACTION = CREATE
    const newPost = await createPost({
      ...value,
      userId: user.id,
    });

    if (!newPost) {
      console.log("Post failed")
    }
    navigate("/");
  };

  return (
    <form className="flex flex-col gap-9 w-full max-w-5xl" {...form} onSubmit={form.handleSubmit(handleSubmit)} control={form.control}>
      <div className="space-y-12">
        <div className="border-b border-gray-900/10 pb-12">

          <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
          <div className="sm:col-span-8">
              <label className="shad-form_label">
                Post Title
              </label>
              <div className="mt-2">
                 
                  <textarea
                  className='custom-scrollbar shad-form_message w-full h-10  bg-dark-3 rounded-xl border-none focus-visible:ring-1 focus-visible:ring-offset-1 ring-offset-light-3'
                    type="text"
                    name="title"
                    id="tile"
                    autoComplete="title"
                    placeholder="Write your post title here"
                  />
              </div>
            </div>

            <div className="sm:col-span-8">
              <label className="shad-form_label">
                Post Description
              </label>
              <div className="mt-2">
                 
                  <textarea
                  className='shad-textarea custom-scrollbar shad-form_message w-full'
                    type="text"
                    name="description"
                    id="description"
                    autoComplete="description"
                    placeholder="Write your post description here"
                  />
              </div>
            </div>

            <div className="sm:col-span-8">
              <label className="shad-form_label">
                Add Photos
              </label>
              <div className="mt-2">
                 <FileUploader
                 fieldChange={''}
                 mediaUrl={''}/>
              </div>
            </div>

          </div>
        </div>
      </div>

      <div className="mt-6 flex items-center justify-end gap-x-6">
        <button type="button" className="text-sm font-semibold leading-6 text-white" onClick={() => navigate('/create-post')}>
          Cancel
        </button>
        <button
          type="submit"
          className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
        >
          Submit
        </button>
      </div>
    </form>
  )
}
