import { createTheme, ThemeProvider } from '@mui/material/styles';
import Link from '@mui/material/Link';
import styled from 'styled-components';
import { Button } from '@mui/material';

const Image = styled.img`
    width: 100%;
    height: 180px;
    background-color: ${({ theme }) => theme.white};
    border-radius: 10px;
    box-shadow: 0 0 16px 2px rgba(0,0,0,0.3);
`

const darkTheme = createTheme({
    palette: {
      mode: 'dark', // Set the mode to 'dark'
      primary: {
        main: '#1F1F22', // Your desired primary color for dark mode
      },
      secondary: {
        main: '#ffc107', // Your desired secondary color for dark mode
      },
      background: {
        default: '#000000', // Dark background color
        paper: '#424242', // Background color for components on top of dark background
      },    text: {
        primary: '#fff', // Light color for text
      },
    },
  });

function Topbar() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
  const handleSignIn = () => {
    setIsLoggedIn(true);
  };

  const handleSignOut = () => {
    setIsLoggedIn(false);
  };
  return (
    <ThemeProvider theme={darkTheme}>
    <nav className='topbar'>
      <Link href='/' className='flex items-center gap-4'>
        <Image src='/logo.svg' alt='logo' width={28} height={28} />
        <p className='text-heading3-bold text-light-1 max-xs:hidden'>Knowledge Hive</p>
      </Link>

      <div className='flex items-center gap-1'>
        <div className='block md:hidden'>
        {isLoggedIn ? (
        <Button variant="contained" onClick={handleSignOut}>
          Sign Out
        </Button>
      ) : (
        <Button variant="contained" onClick={handleSignIn}>
          Sign In
        </Button>
      )}
        </div>
      </div>
    </nav>
    </ThemeProvider>
  );
}

export default Topbar;
