  return (
    <section>
      <ProfileHeader/>
      <div className="mt-9">
      {posts.map((post) => (
              <PostCard
                key={post.id}
                id={post.id}
                currentUserId={user.username}
                parentId={post.parentId}
                content={post.text}
                author={post.author}
                createdAt={post.createdAt}
                comments={post.children}
                isComment={post.isComment}
              />
            ))}
      </div>
    </section>
  );
}
export default Profile;
