import { Link, NavLink, useLocation, useNavigate } from "react-router-dom";

import { sidebarLinks } from "../constant/index";
import Button from "@mui/material/Button";
import { ThemeProvider } from "@mui/material/styles";
import { createTheme } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    mode: "dark", // Enables dark mode
    primary: {
      main: "#3f51b5", // Your custom dark primary color
    },
    secondary: {
      main: "#f57c00", // Your custom dark secondary color
    },
    background: {
      default: "#0d0d0d", // Dark background color
    },
    text: {
      primary: "#fff", // White text for better contrast
    },
  },
});

const LeftSidebar = () => {
  const navigate = useNavigate();
  const { pathname } = useLocation();
  //const { user, setUser, setIsAuthenticated, isLoading } = useUserContext();

  //const { mutate: signOut } = useSignOutAccount();

  const handleSignOut = async (e) => {
    e.preventDefault();
    //signOut();
    //setIsAuthenticated(false);
    //setUser('');
    navigate("/sign-in");
  };

  return (
    <ThemeProvider theme={theme}>
      <nav className="leftsidebar">
        <h1></h1>
      </nav>
    </ThemeProvider>
  );
};

export default LeftSidebar;
