import { Link, NavLink, useLocation, useNavigate } from "react-router-dom";

import { sidebarLinks } from "../constant/index";
import Button from "@mui/material/Button";
import { createTheme } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    mode: "dark", // Enables dark mode
    primary: {
      main: "#3f51b5", // Your custom dark primary color
    },
    secondary: {
      main: "#f57c00", // Your custom dark secondary color
    },
    background: {
      default: "#0d0d0d", // Dark background color
    },
    text: {
      primary: "#fff", // White text for better contrast
    },
  },
});

const LeftSidebar = () => {
  const navigate = useNavigate();
  const { pathname } = useLocation();
  //const { user, setUser, setIsAuthenticated, isLoading } = useUserContext();

  //const { mutate: signOut } = useSignOutAccount();

  const handleSignOut = async (e) => {
    e.preventDefault();
    //signOut();
    //setIsAuthenticated(false);
    //setUser('');
    navigate("/sign-in");
  };

  return (
      <nav className="leftsidebar" sx={{backgroundColor:theme.palette.background.default}}>
        <div className="flex flex-col gap-11">
          <Link to="/" className="flex gap-3 items-center">
            <img
              src=""
              alt="logo"
              width={170}
              height={36}
            />
          </Link>

          {/*  {isLoading || !user.email ? (
            <div className="h-14">
              <Loader />
            </div>
          ) : (
            <Link to={""} className="flex gap-3 items-center">
              <div className="flex flex-col">
                <p className="body-bold">{""}</p>
                <p className="small-regular text-light-3">@{""}</p>
              </div>
            </Link>
          )}
        */}
          <Link to={""} className="flex gap-3 items-center">
            <div className="flex flex-col">
              <p className="body-bold">{""}</p>
              <p className="small-regular text-light-3">@{""}</p>
            </div>
          </Link>
          <ul className="flex flex-col gap-6">
            {sidebarLinks.map((link) => {
              const isActive = pathname === link.route;

              return (
                <li
                  key={link.label}
                  className={`leftsidebar-link group ${
                    isActive && "bg-primary-500"
                  }`}
                >
                  <NavLink
                    to={link.route}
                    className="flex gap-4 items-center p-4"
                  >
                    <img
                      src={link.imgURL}
                      alt={link.label}
                      className={`group-hover:invert-white ${
                        isActive && "invert-white"
                      }`}
                    />
                    {link.label}
                  </NavLink>
                </li>
              );
            })}
          </ul>
        </div>

        <Button
          variant="contained"
          sx={{ mt: 2, mb: 1,backgroundColor:theme.palette.background.default }}
          onClick={(e) => handleSignOut(e)}
        >
          <p className="small-medium lg:base-medium">Logout</p>
        </Button>
      </nav>
  );
};

export default LeftSidebar;
