import React from 'react'
import Link from "@mui/material/Link"
import heart from "../../assets/icons/heart-gray.svg"
import reply from "../../assets/icons/reply.svg"
import share from "../../assets/icons/share.svg"
import { posts } from '../../constant'

function PostCard({
  id,
  currentUserId,
  parentId,
  content,
  author,
  community,
  createdAt,
  comments,
  isComment,
}) {
  return (
    <article
      className={`flex w-full flex-col rounded-xl ${
        isComment ? "px-0 xs:px-7" : "bg-dark-2 p-7"
      }`}
    >
      <div className='flex items-start justify-between'>
        <div className='flex w-full flex-1 flex-row gap-4'>
          <div className='flex flex-col items-center'>
            <Link href={`/profile/${author.id}`} className='relative h-11 w-11'>
              <img
                src={author.image}
                alt='user_community_image'
                fill
                className='cursor-pointer rounded-full'
              />
            </Link>

            <div className='post-card_bar' />
          </div>

          <div className='flex w-full flex-col'>
            <Link href={`/profile/${author.id}`} className='w-fit no-underline'>
              <h4 className='cursor-pointer text-bold text-light-1'>
              {author.name}
              </h4>
            </Link>

            <p className='mt-2 text-small-regular text-light-2'>{cont}</p>

            <div className="mb-10 mt-5 flex flex-col gap-3">
              <div className='flex gap-3.5'>
                <img
                  src={heart}
                  alt='heart'
                  width={24}
                  height={24}
                  className='cursor-pointer object-contain'
                />
                <Link href={''}>
                  <img
                    src={reply}
                    alt='reply'
                    width={24}
                    height={24}
                    className='cursor-pointer object-contain'
                  />
                </Link>
                
                <img
                  src={share}
                  alt='share'
                  width={24}
                  height={24}
                  className='cursor-pointer object-contain'
                />
              </div>

                <Link href={''} className='no-underline'>
                  <p className='mt-1 text-subtle-medium text-gray-1'>
                    reply
                  </p>
                </Link>
            </div>
          </div>
        </div>
{/* 
        <DeleteThread
          threadId={JSON.stringify(id)}
          currentUserId={currentUserId}
          authorId={author.id}
          parentId={parentId}
          isComment={isComment}
        />
        */}
      </div>

        <div className='ml-1 mt-3 flex items-center gap-2'>
            <img
              key={''}
              src={''}
              alt={''}
              width={24}
              height={24}
              className="-ml-5 rounded-full object-cover"
            />

          <Link href={''}>
            <p className='mt-1 text-subtle-medium text-gray-1'>
              reply
            </p>
          </Link>
        </div>

        <Link
          href={''}
          className='mt-5 flex items-center'
        >

          <img
            src={''}
            alt={''}
            width={14}
            height={14}
            className='ml-1 rounded-full object-cover'
          />
        </Link>
    </article>
  )
}

export default PostCard;
