import { Fragment } from 'react'
import { Disclosure, Menu, Transition } from '@headlessui/react'
import { Bars3Icon, BellIcon, XMarkIcon } from '@heroicons/react/24/outline'
import Button from '@mui/material/Button';
import { HamburgerMenu } from '../design/Header';
import { useState } from 'react';
import {navigation} from "../constant"

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

export default function Header() {
  const pathname = useLocation();
  const [openNavigation, setOpenNavigation] = useState(false);
  const toggleNavigation = () => {
    if (openNavigation) {
      setOpenNavigation(false);
      //enablePageScroll();
    } else {
      setOpenNavigation(true);
     // disablePageScroll();
    }
  };

  const handleClick = () => {
    if (!openNavigation) return;

   // enablePageScroll();
    setOpenNavigation(false);
  };
  return (
    <Disclosure as="nav" className={`fixed top-0 left-0 w-full z-50 lg:backdrop-blur-sm  ${openNavigation ? "bg-black" : "bg-black backdrop-blur-sm"}`}>
      {({ open }) => (
        <>
          <div className="mx-auto max-w-7xl px-2 sm:px-6 lg:px-8">
            <div className="relative flex h-16 items-center justify-between">
              <div className="absolute inset-y-0 left-0 flex items-center sm:hidden">
                {/* Mobile menu button*/}
                <Disclosure.Button className="relative inline-flex items-center justify-center rounded-md p-2 text-gray-400 hover:bg-gray-700 hover:text-white focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white">
                  <span className="absolute -inset-0.5" />
                  <span className="sr-only">Open main menu</span>
                  {open ? (
                    <HamburgerMenu/>
                  ) : (
                    <Bars3Icon className="block h-6 w-6" aria-hidden="true" />
                  )}
                </Disclosure.Button>
              </div>
              <div className="flex flex-1 items-center justify-center sm:items-stretch sm:justify-start">
                <div className="flex flex-shrink-0 items-center text-xl text-violet-400 ">
                  Knowledge Hive
                </div>
                <div className={`${
            openNavigation ? "flex" : "hidden"
          } fixed top-[5rem] left-0 right-0 bottom-0 bg-n-8 lg:static lg:flex lg:mx-auto lg:bg-transparent`}>
                  <div className="flex space-x-4">
                  {navigation.map((item) => (
              <a
                key={item.id}
                href={item.url}
                onClick={handleClick}
                className={`block relative font-code text-2xl uppercase text-n-1 transition-colors hover:text-color-1 ${
                  item.onlyMobile ? "lg:hidden" : ""
                } px-6 py-6 md:py-8 lg:-mr-0.25 lg:text-xs lg:font-semibold ${
                  item.url === pathname.hash
                    ? "z-2 lg:text-n-1"
                    : "lg:text-n-1/50"
                } lg:leading-5 lg:hover:text-n-1 xl:px-12`}
              >
                {item.title}
              </a>
            ))}
                  </div>
                </div>
              </div>
              <div className="inset-y-0 right-0 flex items-center pr-2 sm:static sm:inset-auto sm:ml-6 sm:pr-0 relative ml-3">
              <button>Sign Up</button>

                {/* Profile dropdown */}
                <Menu as="div" className="relative ml-3">
                  <div>
                    <button className='bg-violet-400 pt-2 pb-2 pr-3 pl-3 rounded-md mr-3 ml-4'>Login</button>
                  </div>
                  
                </Menu>
              </div>
            </div>
          </div>

          
        </>
      )}
    </Disclosure>
  )
}
